sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
     const {
        snHttp
    } = helpers;

     snHttp('/api/now/table/x_safia_contacts_relationship_contact?sysparm_limit=1&sysparm_query=sys_id=' + event.payload.sysid , {
        method: "GET",
        batch: false,
    }).then(response => {
        if (response.response.result.length > 0) {
          var contact = response.response.result[0].contact_name.value;
          helpers.navigate.to("profile-landing",{"sysId":contact});
        }
    }).catch(error => {
        console.log('Error');
    });

}
